import { useState } from 'react';
import { parseEther } from 'ethers';

const CreateNFT = ({ contract, onCreate }) => {
  const [imageHash, setImageHash] = useState('');
  const [price, setPrice] = useState('');
  const [isCreating, setIsCreating] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!contract || !imageHash || !price) return;

    try {
      setIsCreating(true);
      const tx = await contract.createNFT(
        imageHash,
        parseEther(price)
      );
      await tx.wait();
      setImageHash('');
      setPrice('');
      onCreate();
    } catch (error) {
      console.error("Error creating NFT:", error);
      alert("Error creating NFT: " + error.message);
    } finally {
      setIsCreating(false);
    }
  };

  return (
    <section className="create-nft-section">
      <h2>Create New NFT</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>IPFS Image Hash:</label>
          <input
            type="text"
            value={imageHash}
            onChange={(e) => setImageHash(e.target.value)}
            placeholder="Qm..."
            required
          />
        </div>
        <div className="form-group">
          <label>Price (ETH):</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="0.01"
            min="0.01"
            step="0.01"
            required
          />
        </div>
        <button type="submit" disabled={isCreating}>
          {isCreating ? 'Creating...' : 'Create NFT'}
        </button>
      </form>
    </section>
  );
};

export default CreateNFT;
