import { useState } from 'react';
import { parseEther } from 'ethers';

const CreateNFT = ({ contract, onCreate }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState('');
  const [isCreating, setIsCreating] = useState(false);
  const [status, setStatus] = useState('');

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.size > 100 * 1024 * 1024) {
      alert('File too large (max 100MB)');
      return;
    }

    if (!file.type.match('image.*')) {
      alert('Only image files are allowed');
      return;
    }

    setImage(file);
    setPreview(URL.createObjectURL(file));
  };

  const uploadToPinata = async () => {
    if (!image) return null;
    
    setStatus('Uploading to Pinata...');
    const formData = new FormData();
    formData.append('file', image);

    const metadata = JSON.stringify({
      name: name || 'NFT Image',
      description: description || 'NFT description'
    });
    formData.append('pinataMetadata', metadata);

    try {
      const response = await fetch('https://api.pinata.cloud/pinning/pinFileToIPFS', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_PINATA_JWT}`,
        },
        body: formData,
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error?.details || 'Failed to upload to Pinata');
      }

      const data = await response.json();
      return data.IpfsHash;
    } catch (error) {
      console.error('Error uploading to Pinata:', error);
      setStatus(`Upload failed: ${error.message}`);
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!contract || !image || !price) return;

    try {
      setIsCreating(true);
      setStatus('Starting NFT creation...');
      
      const ipfsHash = await uploadToPinata();
      if (!ipfsHash) {
        setStatus('Pinata upload failed');
        return;
      }

      setStatus('Creating NFT on blockchain...');
      const tx = await contract.createNFT(
        name || 'Unnamed NFT',
        description || 'No description',
        ipfsHash,
        parseEther(price)
      );
      
      await tx.wait();
      setName('');
      setDescription('');
      setPrice('');
      setImage(null);
      setPreview('');
      setStatus('NFT created successfully!');
      onCreate();
    } catch (error) {
      console.error("Error creating NFT:", error);
      setStatus(`Error: ${error.message}`);
    } finally {
      setIsCreating(false);
    }
  };

  return (
    <section className="w-full max-w-lg mx-auto p-6 bg-white rounded-2xl shadow-lg border border-gray-200">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">üöÄ Create New NFT</h2>
      
      <form onSubmit={handleSubmit} className="space-y-5">
        <div className="form-group">
          <label className="block text-gray-700 font-medium mb-1">NFT Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="My Awesome NFT"
            className="w-full px-4 py-2 border rounded-xl shadow-sm focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>

        <div className="form-group">
          <label className="block text-gray-700 font-medium mb-1">Description:</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Describe your NFT"
            rows="3"
            className="w-full px-4 py-2 border rounded-xl shadow-sm focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>

        <div className="form-group">
          <label className="block text-gray-700 font-medium mb-1">NFT Image:</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            required
            className="block w-full text-sm text-gray-700 border rounded-xl cursor-pointer file:mr-3 file:py-2 file:px-4 file:rounded-xl file:border-0 file:text-sm file:font-semibold file:bg-indigo-600 file:text-white hover:file:bg-indigo-700"
          />
          {preview && (
            <div className="image-preview mt-3">
              <img src={preview} alt="Preview" className="max-w-[200px] rounded-lg shadow-md" />
            </div>
          )}
        </div>

        <div className="form-group">
          <label className="block text-gray-700 font-medium mb-1">Price (ETH):</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="0.01"
            min="0.01"
            step="0.01"
            required
            className="w-full px-4 py-2 border rounded-xl shadow-sm focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>

        <button
          type="submit"
          disabled={isCreating}
          className="w-full py-3 bg-indigo-600 text-white font-semibold rounded-xl shadow-md hover:bg-indigo-700 transition duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isCreating ? '‚è≥ Creating...' : '‚ú® Create NFT'}
        </button>

        {status && <p className="text-center text-sm text-gray-600 mt-3">{status}</p>}
      </form>
    </section>
  );
};

export default CreateNFT;
