import { useEffect, useState } from 'react';
import NFTCard from './NFTCard';

const Marketplace = ({ contract, account }) => {
  const [nfts, setNfts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (contract) {
      fetchAllNFTs();
    }
  }, [contract]);

  const fetchAllNFTs = async () => {
    try {
      setLoading(true);
      // First get total NFT count
      const total = await contract.getTotalNFTs();
      
      // Then fetch each NFT
      const items = [];
      for (let i = 1; i <= total; i++) {
        const nft = await contract.nfts(i);
        if (nft.id.toNumber() > 0) { // Only add valid NFTs
          items.push(nft);
        }
      }

      setNfts(items);
    } catch (err) {
      console.error("Error fetching NFTs:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="marketplace">
      <h2>NFT Marketplace</h2>
      
      {loading ? (
        <p>Loading NFTs...</p>
      ) : nfts.length === 0 ? (
        <p>No NFTs available in the marketplace</p>
      ) : (
        <div className="nft-grid">
          {nfts.map((nft, idx) => (
            <NFTCard
              key={idx}
              nft={nft}
              contract={contract}
              account={account}
              onBuy={fetchAllNFTs}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Nft;
