import { useState } from 'react';
import { parseEther } from 'ethers';

const CreateNFT = ({ contract, onCreate }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState('');
  const [isCreating, setIsCreating] = useState(false);
  const [status, setStatus] = useState('');

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.size > 100 * 1024 * 1024) {
      alert('File too large (max 100MB)');
      return;
    }

    if (!file.type.match('image.*')) {
      alert('Only image files are allowed');
      return;
    }

    setImage(file);
    setPreview(URL.createObjectURL(file));
  };

  const uploadToPinata = async () => {
    if (!image) return null;
    
    setStatus('Uploading to Pinata...');
    const formData = new FormData();
    formData.append('file', image);

    const metadata = JSON.stringify({
      name: name || 'NFT Image',
      description: description || 'NFT description'
    });
    formData.append('pinataMetadata', metadata);

    try {
      const response = await fetch('https://api.pinata.cloud/pinning/pinFileToIPFS', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_PINATA_JWT}`,
        },
        body: formData,
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error?.details || 'Failed to upload to Pinata');
      }

      const data = await response.json();
      return data.IpfsHash;
    } catch (error) {
      console.error('Error uploading to Pinata:', error);
      setStatus(`Upload failed: ${error.message}`);
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!contract || !image || !price) return;

    try {
      setIsCreating(true);
      setStatus('Starting NFT creation...');
      
      const ipfsHash = await uploadToPinata();
      if (!ipfsHash) {
        setStatus('Pinata upload failed');
        return;
      }

      setStatus('Creating NFT on blockchain...');
      const tx = await contract.createNFT(
        name || 'Unnamed NFT',
        description || 'No description',
        ipfsHash,
        parseEther(price)
      );
      
      await tx.wait();
      setName('');
      setDescription('');
      setPrice('');
      setImage(null);
      setPreview('');
      setStatus('NFT created successfully!');
      onCreate();
    } catch (error) {
      console.error("Error creating NFT:", error);
      setStatus(`Error: ${error.message}`);
    } finally {
      setIsCreating(false);
    }
  };

  const getStatusColor = () => {
    if (status.includes('success')) return "bg-green-100 text-green-700 border-green-300";
    if (status.includes('Error') || status.includes('failed')) return "bg-red-100 text-red-700 border-red-300";
    return "bg-blue-100 text-blue-700 border-blue-300";
  };

  return (
    <section className="w-full max-w-2xl mx-auto p-8 rounded-2xl shadow-2xl border border-gray-200 bg-gradient-to-br from-white/80 to-gray-100/60 backdrop-blur-lg">
      {/* Title */}
      <h2 className="text-3xl font-extrabold text-center bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent mb-8">
        üé® Create Your NFT
      </h2>
      
      {/* Form */}
      <form onSubmit={handleSubmit} className="space-y-6">
        
        <div>
          <label className="block text-gray-700 font-semibold mb-2">NFT Name</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="My Awesome NFT"
            className="w-full px-4 py-3 border rounded-xl shadow-sm focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition"
          />
        </div>

        <div>
          <label className="block text-gray-700 font-semibold mb-2">Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Describe your NFT..."
            rows="3"
            className="w-full px-4 py-3 border rounded-xl shadow-sm focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition"
          />
        </div>

        <div>
          <label className="block text-gray-700 font-semibold mb-2">NFT Image</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            required
            className="block w-full text-sm text-gray-600 border rounded-xl cursor-pointer file:mr-4 file:py-2 file:px-5 file:rounded-lg file:border-0 file:font-semibold file:bg-gradient-to-r file:from-indigo-500 file:to-purple-600 file:text-white hover:file:opacity-90 transition"
          />
          {preview && (
            <div className="mt-4 flex justify-center">
              <img src={preview} alt="Preview" className="max-h-48 rounded-xl shadow-lg border border-gray-200" />
            </div>
          )}
        </div>

        <div>
          <label className="block text-gray-700 font-semibold mb-2">Price (ETH)</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="0.01"
            min="0.01"
            step="0.01"
            required
            className="w-full px-4 py-3 border rounded-xl shadow-sm focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition"
          />
        </div>

        <button
          type="submit"
          disabled={isCreating}
          className="w-full py-3 text-lg font-semibold rounded-xl shadow-lg bg-gradient-to-r from-indigo-600 to-purple-600 text-white hover:scale-105 transform transition duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isCreating ? '‚è≥ Creating...' : '‚ú® Mint NFT'}
        </button>

        {status && (
          <p className={`mt-4 p-3 text-center rounded-lg border text-sm font-medium ${getStatusColor()}`}>
            {status}
          </p>
        )}
      </form>
    </section>
  );
};

export default CreateNFT;
