import { useState, useEffect } from 'react';
import { BrowserProvider, Contract, formatEther } from 'ethers';
import './App.css';
import Navbar from './components/Navbar';
import NFTCard from './components/NFTCard';
import CreateNFT from './components/CreateNFT';
import contractABI from './contracts/SimpleNFTMarketplace.json';

function App() {
  const [account, setAccount] = useState('');
  const [nfts, setNfts] = useState([]);
  const [contract, setContract] = useState(null);
  const [loading, setLoading] = useState(true);

  const contractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

  const connectWallet = async () => {
    try {
      if (!window.ethereum) {
        alert("Please install MetaMask!");
        return;
      }

      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setAccount(accounts[0]);

      const provider = new BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const nftContract = new Contract(contractAddress, contractABI.abi, signer);
      setContract(nftContract);

      await loadNFTs(nftContract);
    } catch (error) {
      console.error("Error connecting wallet:", error);
    }
  };

  const loadNFTs = async (contract) => {
    try {
      setLoading(true);
      // First get total count
      const total = await contract.getTotalNFTs();
      console.log("Total NFTs:", total.toString());
for (let i = 1; i <= total; i++) {
  try {
    const nft = await contract.nfts(i);
    console.log(`NFT ${i}:`, nft);
    items.push(nft);
  } catch (err) {
    console.warn(`Error loading NFT ${i}:`, err);
  }
}


      // Then fetch each NFT individually for better reliability
      const items = [];
      for (let i = 1; i <= total; i++) {
        try {
          const nft = await contract.nfts(i);
          if (nft.id.toNumber() > 0) { // Only add valid NFTs
            items.push(nft);
          }
        } catch (err) {
          console.warn(`Error loading NFT ${i}:`, err);
        }
      }

      setNfts(items);
    } catch (error) {
      console.error("Error loading NFTs:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateNFT = () => {
    loadNFTs(contract);
  };

  useEffect(() => {
    const checkWalletConnection = async () => {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          setAccount(accounts[0]);
          const provider = new BrowserProvider(window.ethereum);
          const signer = await provider.getSigner();
          const nftContract = new Contract(contractAddress, contractABI.abi, signer);
          setContract(nftContract);
          await loadNFTs(nftContract);
        }
      }
    };

    checkWalletConnection();
  }, []);

  return (
    <div className="app">
      <Navbar account={account} connectWallet={connectWallet} />

      <main className="main-content">
        <CreateNFT contract={contract} onCreate={handleCreateNFT} />

        <section className="marketplace-section">
          <h2>NFT Marketplace</h2>
          {loading ? (
            <div className="loading">Loading NFTs...</div>
          ) : nfts.length === 0 ? (
            <div className="empty-state">No NFTs found in marketplace</div>
          ) : (
            <div className="nft-grid">
              {nfts.map((nft, index) => (
                <NFTCard 
                  key={index}
                  nft={nft}
                  contract={contract}
                  account={account}
                  onBuy={() => loadNFTs(contract)}
                />
              ))}
            </div>
          )}
        </section>
      </main>
    </div>
  );
}

export default App;
